const api={get:function(n,e={}){return new Promise(function(t,o){let r={method:"GET",headers:{"Content-Type":"application/json",...e}};fetch(config.api_app_url+n,r).then(n=>n.json()).then(function(n){t(n)}).catch(n=>{o({resError:n,message:"Server not responding!"})})})},post:function(n,e,t={}){return new Promise(function(o,r){let i={method:"POST",headers:{"Content-Type":"application/json",...t},body:JSON.stringify(e)};fetch(config.api_app_url+n,i).then(n=>n.json()).then(function(n){o(n)}).catch(n=>{r({resError:n,message:"Server not responding!"})})})}};